segmentScript.script:
{
  "segments": [
    {
      "timeStart": 0,
      "timeEnd": 4,
      "subtitle": "三十三载宁夏老品牌，汇聚北纬38度精华，共筑黄金奶源盛宴。",
      "intention": "品牌底蕴展示与产地背书",
      "coreAction": "镜头由远及近穿过云层，展现宏伟古建筑背景下的各朝代旗帜转换。",
      "elementTransformation": "旗帜从古代图腾转化为“夏进”品牌标识及“北纬38°”字样。",
      "visualChange": "金光流转在古建筑檐角，云海翻腾呈现出厚重的历史感。",
      "contentStyle": "宏大史诗感，融合传统文化与地域品牌文化。",
      "sceneStyle": "云雾缭绕的南天门式宫殿，两侧立有巨大的奶源地标识柱。",
      "description": "镜头在大气的古风BGM中推进，展现宁夏牧场的宏伟景观，五个石柱上的朝代标识最终汇聚成夏进纯牛奶的品牌Logo。",
      "shootingStyle": "大远景俯冲转中景平移，利用动态云层作为转场。",
      "keyFrames": {
        "storyBeginning": {
          "timestamp": 0.5,
          "desc": "云雾散开，露出刻有“夏进三十三年”字样的宏伟牌楼。"
        },
        "climax": {
          "timestamp": 2.2,
          "desc": "五面代表高品质的旗帜在宫殿前依次亮起，象征金牌奶源。"
        },
        "storyEnding": {
          "timestamp": 3.8,
          "desc": "镜头定格在“北纬38度黄金奶源带”的金色字样上。"
        }
      }
    },
    {
      "timeStart": 4,
      "timeEnd": 10,
      "subtitle": "塞上牧场，匠心坚守。只选纯净生牛乳，这一口是来自大地的礼赠。",
      "intention": "强调配料纯净与产品源头",
      "coreAction": "浩荡的秦俑军队在古长城背景下行进，旗帜飘扬，文字浮现。",
      "elementTransformation": "原本的紫色秦旗变为带有“生牛乳0添加”字样的夏进品牌旗帜。",
      "visualChange": "广袤的平原上，尘土飞扬变为乳白色的光晕波动，预示产品纯净。",
      "contentStyle": "气势磅礴，英雄凯旋式叙事。",
      "sceneStyle": "雄伟的长城脚下，紫色军阵象征着夏进品牌坚不可摧的品质堡垒。",
      "description": "俯瞰视角，密集的兵马俑军阵整齐划一地向前推进，上方悬浮“配料表仅有生牛乳”的巨大艺术字。",
      "shootingStyle": "高空俯拍配合快速推拉镜头，制造强烈的压迫感与视觉冲击。",
      "keyFrames": {
        "storyBeginning": {
          "timestamp": 4.5,
          "desc": "秦俑军阵在长城下行进，手中旗帜赫然写着“夏进”二字。"
        },
        "climax": {
          "timestamp": 7.3,
          "desc": "“生牛乳0添加”金色大字在阵型上方爆开，震撼人心。"
        },
        "storyEnding": {
          "timestamp": 9.5,
          "desc": "镜头拉近至领军秦俑，其身姿挺拔，象征品牌硬核实力。"
        }
      }
    },
    {
      "timeStart": 10,
      "timeEnd": 14,
      "subtitle": "243ml便携旋盖，即刻开启浓郁醇香。营养随行，更有活力。",
      "intention": "产品卖点（便携、锁鲜）展示",
      "coreAction": "兵马俑将领手中举起夏进243ml瓶装纯牛奶，特写展示旋盖开启过程。",
      "elementTransformation": "古代陶罐在士兵手中瞬间变换为蓝白配色的夏进牛奶瓶。",
      "visualChange": "瓶身反射出温润的白色光泽，旋开盖子时散发出诱人的白色雾气。",
      "contentStyle": "拟人化幽默，通过古今对比突出便携特征。",
      "sceneStyle": "威武的将领坐骑旁，瓶装奶整齐排列，展现“现代补给”感。",
      "description": "兵马俑士兵单手持瓶，特写镜头捕捉到旋开盖子的瞬间，牛奶瓶的设计与兵俑质感形成强烈视觉冲击。",
      "shootingStyle": "近景特写配合微距，强调瓶身细节与旋盖的质感。",
      "keyFrames": {
        "storyBeginning": {
          "timestamp": 10.2,
          "desc": "镜头推向手持夏进牛奶的兵马俑将领，展现产品现代感包装。"
        },
        "climax": {
          "timestamp": 12.5,
          "desc": "瓶盖旋转拧开，发出清脆的开盖音效，牛奶色泽纯净。"
        },
        "storyEnding": {
          "timestamp": 13.8,
          "desc": "将领露出满意的表情，牛奶瓶在光影下熠熠生辉。"
        }
      }
    },
    {
      "timeStart": 14,
      "timeEnd": 16,
      "subtitle": "3.2g优质乳蛋白，105mg高钙。每一滴都醇厚丝滑，回味无穷。",
      "intention": "营养成分展示与口感特写",
      "coreAction": "慢动作捕捉牛奶从瓶口倾倒而出的过程，激起层层奶浪。",
      "elementTransformation": "倾倒的液体中浮现“3.2g蛋白”与“高钙”等关键数据。",
      "visualChange": "奶液质地丝滑如绸缎，呈现微黄的天然色泽，富有质感。",
      "contentStyle": "质感生活，强调产品内在的高品质。",
      "sceneStyle": "纯净的木质器皿承接奶液，背景柔和，突出牛奶的纯正。",
      "description": "特写镜头下，洁白细腻的奶液倾泻而下，展现出夏进牛奶极佳的粘稠度与丝滑感，数据光效萦绕其中。",
      "shootingStyle": "极高帧率慢动作拍摄，捕捉液体的微小波动。",
      "keyFrames": {
        "storyBeginning": {
          "timestamp": 14.1,
          "desc": "奶液开始从瓶口流出，质地浓稠且丝滑。"
        },
        "climax": {
          "timestamp": 15.2,
          "desc": "液体冲击杯底激起奶浪，“3.2g蛋白质”金色数字在杯中浮现。"
        },
        "storyEnding": {
          "timestamp": 15.9,
          "desc": "杯中牛奶平静下来，表面光亮如镜，预示高品质。"
        }
      }
    },
    {
      "timeStart": 16,
      "timeEnd": 20,
      "subtitle": "夏进纯牛奶整箱装，守护全家健康。三十三年匠心，领券下单更划算！",
      "intention": "利益点引导与转化下单",
      "coreAction": "两名兵马俑举瓶碰杯欢庆，画面过渡到24瓶整箱装的产品大集合。",
      "elementTransformation": "欢庆场景瞬间缩小，置入右侧展示框，中心位置出现大礼盒装。",
      "visualChange": "画面色彩由古朴转为亮丽，促销字样跳动，吸引用户注意力。",
      "contentStyle": "欢快热烈，充满节日馈赠氛围。",
      "sceneStyle": "金色背景前，大箱装夏进牛奶占据中心，周围环绕着红包与领券提示。",
      "description": "秦俑举瓶对饮笑逐颜开，背景出现“24瓶整箱更省心”的标语，引导用户快速下单。",
      "shootingStyle": "中景碰杯转全景产品堆头，最后以静物大特写收尾。",
      "keyFrames": {
        "storyBeginning": {
          "timestamp": 16.5,
          "desc": "两名兵马俑将领豪爽碰瓶对饮，脸上充满喜悦。"
        },
        "climax": {
          "timestamp": 18.2,
          "desc": "24瓶整箱装产品震撼登场，屏幕浮现“周年放价”字样。"
        },
        "storyEnding": {
          "timestamp": 19.8,
          "desc": "夏进品牌Logo与“立即下单”按钮出现在屏幕视觉中心。"
        }
      }
    }
  ]
